package com.basic;
/**
 * member 변수, 메소드, 한 블럭 '{}'에도 static을 붙일 수 있다.
 * @author student
 *
 */
public class StaticTest {
	
	static void hello() {
		System.out.println("Hello");
	}
	
	static {
		//static block은 main함수보다 무조건 먼저 실행된다.
		System.out.println("static block");
	}
	static int scount; //클래스 변수. 데이터가 생성이 되는 곳은 CA(Class Area)에 하나만 생성됨. 모든 곳에서 같이 씀. Runtime전에 이미 만들어짐.
	int count;	//힙에 따로 만들어 짐. 인스턴스변수. 객체 안에서 공유됨. heap에 생성. Runtime시에 만들어짐.
	
	public static void main(String[] args) { //객체를 생성하지 않아도 이미 생성되어 있음. 바로 쓰세요!

		System.out.println(++scount); //객체를 만들지 않아도 사용이 가능. 이미 만들어져있음.
		hello();
		//System.out.println(count++); 사용하려면 객체를 만드는 것이 선행되어야 함. 초기화 하지않고 사용하면 Error
		
		/*StaticTest s1 = new StaticTest();
		StaticTest s2 = new StaticTest();
		StaticTest s3 = new StaticTest();
		
		System.out.println(++s1.count + ":" + ++s2.count +":" + ++s3.count);
		System.out.println(++s1.scount + ":" + ++s2.scount +":" + ++s3.scount);
	*/
	}
	static {
		//static block
		System.out.println("static block closed");
	}
		
}
